import React, { useEffect, useRef, useState } from "react";
import { FilesetResolver, PoseLandmarker, DrawingUtils } from "@mediapipe/tasks-vision";
import Webcam from "react-webcam";

const CameraComponent = ({ onMeasurement, onPoseDetected }) => {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [poseLandmarker, setPoseLandmarker] = useState(null);
  const [isCorrectPose, setIsCorrectPose] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [hasSent, setHasSent] = useState(false); // ✅ Mencegah kirim berulang

  // Inisialisasi model
  useEffect(() => {
    const init = async () => {
      const vision = await FilesetResolver.forVisionTasks(
        "https://cdn.jsdelivr.net/npm/@mediapipe/tasks-vision@latest/wasm"
      );

      const landmarker = await PoseLandmarker.createFromOptions(vision, {
                    baseOptions: {
                        modelAssetPath: "https://storage.googleapis.com/mediapipe-models/pose_landmarker/pose_landmarker_full/float16/latest/pose_landmarker_full.task",
                        delegate: "GPU",
                    },
                    runningMode: "VIDEO",
                    numPoses: 1,
                });


      setPoseLandmarker(landmarker);
    };

    init();
  }, []);

  // Kirim ke backend
  const sendToBackend = async (landmarks) => {
    try {
      setIsLoading(true);
      const response = await fetch("http://localhost:5000/measure", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          landmarks: landmarks.map((lm) => ({
            x: lm.x,
            y: lm.y,
            z: lm.z,
          })),
        }),
      });

      if (!response.ok) throw new Error(await response.text());

      const measurements = await response.json();
      onMeasurement(measurements);
      setHasSent(true); // ✅ Kirim hanya sekali
    } catch (error) {
      console.error("Measurement error:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Validasi pose tegak
  const validatePose = (landmarks) => {
    const leftShoulder = landmarks[11];
    const rightShoulder = landmarks[12];
    const diff = Math.abs(leftShoulder.y - rightShoulder.y);
    const isValid = diff < 0.05;
    setIsCorrectPose(isValid);
    return isValid;
  };

  // Loop video
  useEffect(() => {
    let animationFrame;

    const runDetection = async () => {
      if (
        webcamRef.current &&
        webcamRef.current.video.readyState === 4 &&
        poseLandmarker
      ) {
        const video = webcamRef.current.video;
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        const result = poseLandmarker.detectForVideo(video, performance.now());

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

        const drawingUtils = new DrawingUtils(ctx);

        if (result.landmarks.length > 0) {
          const landmarks = result.landmarks[0];

          // Draw skeleton
          drawingUtils.drawLandmarks(landmarks, {
            color: isCorrectPose ? "#00FF00" : "#FF0000",
            radius: 4,
          });

          // Highlight titik penting
          const BODY_KEYPOINTS = [11, 12, 13, 14, 15, 16, 23, 24, 25, 26]; // plus lainnya jika perlu

            drawingUtils.drawLandmarks(
            landmarks.filter((_, i) => BODY_KEYPOINTS.includes(i)),
            { color: "#00FF00", radius: 4 }
            );


          onPoseDetected(landmarks);

          const valid = validatePose(landmarks);
          if (valid && !isLoading && !hasSent) {
            sendToBackend(landmarks);
          }
        }
      }

      animationFrame = requestAnimationFrame(runDetection);
    };

    if (poseLandmarker) {
      animationFrame = requestAnimationFrame(runDetection);
    }

    return () => {
      cancelAnimationFrame(animationFrame);
    };
  }, [poseLandmarker, isCorrectPose, isLoading, hasSent]);

  return (
    <div className="camera-container" style={{ position: "relative" }}>
      <Webcam
        ref={webcamRef}
        mirrored
        style={{ width: "100%", height: "auto", position: "relative", zIndex: 1 }}
      />
      <canvas
        ref={canvasRef}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 2,
        }}
      />
      {isLoading && (
        <div className="loading-overlay">
          <p>Measuring...</p>
        </div>
      )}
    </div>
  );
};

export default CameraComponent;
