import React, { useEffect, useRef, useState } from "react";
import { FilesetResolver, PoseLandmarker, DrawingUtils } from "@mediapipe/tasks-vision";
import Webcam from "react-webcam";

const CameraComponent = ({ onMeasurement, onPoseDetected, isMeasuring }) => {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [poseLandmarker, setPoseLandmarker] = useState(null);
  const [isCorrectPose, setIsCorrectPose] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [hasSent, setHasSent] = useState(false);

  // 🧠 Jarak antar titik
  const distance = (a, b) =>
    Math.sqrt(Math.pow(a.x - b.x, 2) + Math.pow(a.y - b.y, 2));

  // 🎯 Hitung metrik pengukuran tubuh
  const calculateMeasurements = (landmarks) => {
    const shoulderWidth = distance(landmarks[11], landmarks[12]);
    const hipWidth = distance(landmarks[23], landmarks[24]);
    const leftArmLength =
      distance(landmarks[11], landmarks[13]) + distance(landmarks[13], landmarks[15]);
    const shoulderOutput = convertToCm(shoulderWidth, landmarks, 170)  
    const hipOutput = convertToCm(hipWidth, landmarks, 170)  
    const leftArmOutput = convertToCm(leftArmLength, landmarks, 170)  
    return { shoulderOutput, hipOutput, leftArmOutput };
  };

  const convertToCm = (measurements, landmarks, userHeightCm = 170) => {
    console.log('Measure : ' +measurements)
    const top = landmarks[0]; // nose
    const bottom = landmarks[32]; // foot (kanan)
    const pixelHeight = Math.abs(top.y - bottom.y);
    const scale = userHeightCm / pixelHeight;
    const result= (measurements * scale).toFixed(2)

    return result
    }


  // 🟢 Validasi pose tegak
  const validatePose = (landmarks) => {
    const leftShoulder = landmarks[11];
    const rightShoulder = landmarks[12];
    const diff = Math.abs(leftShoulder.y - rightShoulder.y);
    const isValid = diff < 0.05;
    setIsCorrectPose(isValid);
    return isValid;
  };

  // 🚀 Load model
  useEffect(() => {
    const init = async () => {
      const vision = await FilesetResolver.forVisionTasks(
        "https://cdn.jsdelivr.net/npm/@mediapipe/tasks-vision@latest/wasm"
      );

      const landmarker = await PoseLandmarker.createFromOptions(vision, {
        baseOptions: {
          modelAssetPath:
            "https://storage.googleapis.com/mediapipe-models/pose_landmarker/pose_landmarker_full/float16/latest/pose_landmarker_full.task",
          delegate: "GPU",
        },
        runningMode: "VIDEO",
        numPoses: 1,
      });

      setPoseLandmarker(landmarker);
    };

    init();
  }, []);

  // 📡 Kirim ke backend
  const sendToBackend = async (landmarks) => {
    try {
      setIsLoading(true);
      const response = await fetch("http://localhost:5000/measure", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          landmarks: landmarks.map((lm) => ({ x: lm.x, y: lm.y, z: lm.z })),
        }),
      });

      if (!response.ok) throw new Error(await response.text());

      const data = await response.json();
      onMeasurement(data);
      setHasSent(true);
    } catch (error) {
      console.error("Measurement error:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // 🔁 Render loop
  useEffect(() => {
    let animationFrame;

    const runDetection = async () => {
      if (
        webcamRef.current &&
        webcamRef.current.video.readyState === 4 &&
        poseLandmarker
      ) {
        const video = webcamRef.current.video;
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        const result = poseLandmarker.detectForVideo(video, performance.now());

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.drawImage(video, 0, 0, canvas.width, canvas.height);

        const drawingUtils = new DrawingUtils(ctx);

        if (result.landmarks.length > 0) {
          const landmarks = result.landmarks[0];

          // 🎯 Highlight titik penting tubuh
          const BODY_KEYPOINTS = [11, 12, 13, 14, 15, 16, 23, 24];
          drawingUtils.drawLandmarks(
            landmarks.filter((_, i) => BODY_KEYPOINTS.includes(i)),
            {
              color: isCorrectPose ? "#00FF00" : "#FF0000",
              radius: 4,
            }
          );

          // 🔁 Kirim ke parent
          onPoseDetected(landmarks);

          // ✅ Validasi pose & kirim jika `isMeasuring`
          const valid = validatePose(landmarks);
          if (valid && isMeasuring && !isLoading && !hasSent) {
            const result = calculateMeasurements(landmarks);
            console.log("Calculated:", result);
            onMeasurement(result);
            setHasSent(true);
          }
        }
      }

      animationFrame = requestAnimationFrame(runDetection);
    };

    if (poseLandmarker) {
      animationFrame = requestAnimationFrame(runDetection);
    }

    return () => {
      cancelAnimationFrame(animationFrame);
    };
  }, [poseLandmarker, isCorrectPose, isLoading, isMeasuring, hasSent]);

  return (
    <div className="camera-container" style={{ position: "relative" }}>
      <Webcam
        ref={webcamRef}
        mirrored
        style={{ width: "100%", height: "auto", position: "relative", zIndex: 1 }}
      />
      <canvas
        ref={canvasRef}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 2,
        }}
      />
      {isLoading && (
        <div className="loading-overlay">
          <p>Measuring...</p>
        </div>
      )}
    </div>
  );
};

export default CameraComponent;
