import React, { useState } from "react";
import CameraComponent from "./components/Camera";
import PoseGuide from "./components/PoseGuide";
import Results from "./components/Results";
import "./styles.css";

function App() {
  const [measurements, setMeasurements] = useState(null);
  const [currentPose, setCurrentPose] = useState(null);
  const [isMeasuring, setIsMeasuring] = useState(false);
  const [userHeight, setUserHeight] = useState(null)
  const [heightSubmitted, setHeightSubmitted] = useState(false)
  const handleMeasurementResult = (data) => {
        setMeasurements(data);
        setIsMeasuring(false); // reset tombol
      };


  const handlePoseDetected = (landmarks) => {
    setCurrentPose(landmarks);
    if (isMeasuring) {
      // Send to backend for measurement
      const formattedLandmarks = landmarks.map(lm => ({
        x: lm.x, y: lm.y, z: lm.z
      }));
    }
  };

  const handleUserHeightSubmit = (e) => {
    e.preventDefault();
    if (userHeight && userHeight > 0) {
      setHeightSubmitted(true)
    }
  }

  return (
    <div className="app">
      <h1>WBH - Body Measurement POC</h1>
       {!heightSubmitted ? (
        <div className="height-input-section">
          <h2>Masukkan Tinggi Anda (cm)</h2>
          <form onSubmit={handleUserHeightSubmit}>
            <input
              type="number"
              className="form-control"
              value={userHeight || ""}
              onChange={(e) => setUserHeight(parseFloat(e.target.value))}
              placeholder="Contoh: 170"
              min="100"
              max="250"
              step="0.1"
              required
            />
            <button type="submit">Konfirmasi</button>
          </form>
        </div>
      ) : (
        <div className="container">
          <div className="guide-section">
            <PoseGuide currentPose={currentPose} />
          </div>
          <div className="camera-section">
            <CameraComponent 
              onMeasurement={handleMeasurementResult}
              onPoseDetected={handlePoseDetected}
              isMeasuring={isMeasuring}
              userHeight={userHeight} // Kirim tinggi ke komponen kamera
            />

            <button onClick={() => setIsMeasuring(!isMeasuring)}>
              {isMeasuring ? "Stop Measurement" : "Start Measurement"}
            </button>
          </div>
          <Results measurements={measurements} />
        </div>
      )}
    </div>
  );
}

export default App;
