import React, { useRef, useEffect, useState } from "react";
import { Pose } from "@mediapipe/pose";
import { Camera } from "@mediapipe/camera_utils";
import { drawConnectors, drawLandmarks } from "@mediapipe/drawing_utils";
import { POSE_CONNECTIONS } from "@mediapipe/pose";
import Webcam from "react-webcam";

const CameraComponent = ({ onMeasurement, onPoseDetected }) => {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [isCorrectPose, setIsCorrectPose] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // Fungsi untuk mengirim landmark ke backend
  const sendToBackend = async (landmarks) => {
    try {
      setIsLoading(true);
      const response = await fetch('http://localhost:5000/measure', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          landmarks: landmarks.map(lm => ({ x: lm.x, y: lm.y, z: lm.z }))
        }),
      });

      if (!response.ok) throw new Error(await response.text());

      const measurements = await response.json();
      onMeasurement(measurements);
    } catch (error) {
      console.error("Measurement error:", error);
    } finally {
      setIsLoading(false);
    }
  };

  // Validasi pose
  const validatePose = (landmarks) => {
    const leftShoulder = landmarks[11];
    const rightShoulder = landmarks[12];
    const shoulderLevelDiff = Math.abs(leftShoulder.y - rightShoulder.y);
    const isValid = shoulderLevelDiff < 0.05; // Threshold
    setIsCorrectPose(isValid);
    return isValid;
  };

  // Bisa ditambahkan di Camera.jsx
const drawGuideArrows = (ctx) => {
  if (!isCorrectPose) {
    ctx.beginPath();
    ctx.moveTo(100, 100);
    ctx.lineTo(150, 150);
    ctx.strokeStyle = "#FF0000";
    ctx.lineWidth = 3;
    ctx.stroke();
  }
};

  useEffect(() => {
    const pose = new Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
    });

    pose.setOptions({
      modelComplexity: 2,
      smoothLandmarks: true,
    });

    pose.onResults((results) => {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");

      // Update canvas size
      canvas.width = webcamRef.current.video.videoWidth;
      canvas.height = webcamRef.current.video.videoHeight;

      // Draw camera feed
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(results.image, 0, 0, canvas.width, canvas.height);

      // Draw pose landmarks
      if (results.poseLandmarks) {
        // Visualisasi skeleton
        drawConnectors(ctx, results.poseLandmarks, POSE_CONNECTIONS, {
          color: isCorrectPose ? "#00FF00" : "#FF0000",
          lineWidth: 4,
        });

        // Highlight titik pengukuran
        const keyPoints = [11, 12, 13, 14, 15, 16, 23, 24]; // Bahu, siku, pinggang
        drawLandmarks(ctx, 
          results.poseLandmarks.filter((_, i) => keyPoints.includes(i)), 
          { color: "#FFFF00", radius: 6 }
        );

        // Kirim ke parent component untuk panduan pose
        onPoseDetected(results.poseLandmarks);

        // Validasi pose
        const isValid = validatePose(results.poseLandmarks);

        // Auto-kirim ke backend jika pose benar
        if (isValid && !isLoading) {
          sendToBackend(results.poseLandmarks);
        }
      }
    });

    const camera = new Camera(webcamRef.current.video, {
      onFrame: async () => {
        await pose.send({ image: webcamRef.current.video });
      },
      width: 640,
      height: 480,
    });
    camera.start();

    return () => camera.stop();
  }, [isCorrectPose, isLoading]);

  return (
    <div className="camera-container">
      <Webcam
        ref={webcamRef}
        mirrored
        style={{ width: '100%', height: 'auto' }}
      />
      <canvas
        ref={canvasRef}
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
        }}
      />
      {/* {isLoading && (
        <div className="loading-overlay">
          <p>Measuring...</p>
        </div>
      )} */}
    </div>
  );
};

export default CameraComponent;
